'use strict';
const qtoolsGen = require('qtools');
const qtools = new qtoolsGen(module);
//const crypt=require('crypto');

//START OF moduleFunction() ============================================================

var moduleFunction = function(args) {

	qtools.validateProperties({
		subject: args || {},
		targetScope: this, //will add listed items to targetScope
		propList: [
			{
				name: 'config',
				optional: false
			},
			{
				name: 'router',
				optional: false
			},
			{
				name: 'mongoose',
				optional: false
			},
			{
				name: 'permissionMaster',
				optional: false
			},
			{
				name: 'initCallback',
				optional: false
			}
		]
	});

	//LOCAL VARIABLES ====================================
	const mongoose = this.mongoose;

	//LOCAL FUNCTIONS ====================================

	//METHODS AND PROPERTIES ====================================

	this.shutdown = (message, callback) => {
		console.log(`shutting down ${__dirname}`);
		callback('', message);
	}

	this.get = (criteria, callback) => {
		User.find(criteria, callback);

	}

	//API ENDPOINTS ====================================

	let route = new RegExp('user$');
	this.permissionMaster.addRoute(route, 'admin editor');
	this.router.get(route, (req, res, next) => {

		this.get({}, (err, users) => {
			if (err) {
					next({
						code: '500',
						message: 'Database access problem (users.get())'
					});
			} else {
				res.json({token:this.permissionMaster.updateToken(req), data:users});
			}
		});

	});

	this.router.post(/user/, (req, res, next) => {

		res.set({
			'content-type': 'application/json;charset=ISO-8859-1',
			messageid: qtools.newGuid(),
			messagetype: 'RESPONSE',
			responsesource: 'utilityServer',
			connection: 'Close'
		});

		res.json({
			status: `CONNECTIVITY ESTABLISHED FROM ${this.config.system.name}/${this.config.user}${req.path} GET`,
			headers: req.headers,
			body: req.body,
			query: req.query
		});
	});

	//INITIALIZATION ====================================

	const userSchema = mongoose.Schema({
		first: String,
		last: String,
		username: String,
		pwhash: String,
		role: String,
		debugDataVersion: String
	});

	userSchema.methods.speak = function() {
		const greeting = this.name
			? "Meow name is " + this.name
			: "I don't have a name";
		console.log(greeting);
	}

	const User = mongoose.model('User', userSchema);

	const debugDataList = [
		{
			first: 'nurse',
			last: 'nurse_last',
			username: 'nurse',
			pwhash: qtools.passwordHash('test'),
			role: this.permissionMaster.getRole('nurse').name
		},
		{
			first: 'editor',
			last: 'editor_last',
			username: 'editor',
			pwhash: qtools.passwordHash('test'),
			role: this.permissionMaster.getRole('editor').name
		},
		{
			first: 'admin',
			last: 'admin_last',
			username: 'admin',
			pwhash: qtools.passwordHash('test'),
			role: this.permissionMaster.getRole('admin').name
		},
	];
	const debugDataVersion = 121;

	let debugDataInx = 0;
	let nameSplit = [];
	let userData = {};

	const addUser = () => {

		var user = new User(debugDataList[debugDataInx]);

		debugDataInx = debugDataInx + 1;

		user.save((err, result) => {
			if (err) {
				return console.error(err);
			}
			if (debugDataInx < debugDataList.length) {
				setTimeout(addUser, 100);
			}
		});

	}
	
	User.find({
		debugDataVersion: debugDataVersion
	}, (err, result) => {
		if (!result.length) {
			console.log('Clearing user database');
			User.remove({}, (err, result) => {
				var tmp = new User({
					debugDataVersion: debugDataVersion
				});
				tmp.save(() => {
					console.log('updated debugDataVersion to ' + debugDataVersion);
				});
				User.count((err, result) => {
					console.log('INITIALIZING: TEST USERS');
					addUser();
				});
			});
		} else {
			console.log('test user data is in place');
		}
	});

	this.initCallback();
	return this;
};

//END OF moduleFunction() ============================================================

module.exports = moduleFunction;
//module.exports = new moduleFunction();

